const GameData *getGameData(uint32_t elf_crc)
{
    for (size_t i = 0; i < sizeof(gameData) / sizeof(gameData[0]); ++i)
    {
        if (gameData[i].crc32 == elf_crc)
        {
            return &gameData[i];
        }
    }
    return NULL;
}
